  <v-text-field v-model="form.full_name" label="Name" :rules="rules.required"></v-text-field>
</v-col>
<v-col cols="6" md="4">
  <v-dialog ref="date_dialog" v-model="modals.date" :return-value.sync="form.date" width="300px">
    <template v-slot:activator="{ on, attrs }">
      <v-text-field :value="getFormatDate(form.date)" label="Date" prepend-icon="mdi-calendar" label="Birthdate" :rules="rules.required" readonly v-bind="attrs" v-on="on" :rules="rules.required"></v-text-field>
    </template>
    <v-date-picker v-model="form.date" scrollable>
      <v-spacer></v-spacer>
      <v-btn text color="primary" @click="modals.date = false">
        Cancel
      </v-btn>
      <v-btn text color="primary" @click="$refs.date_dialog.save(form.date);">
        OK
      </v-btn>
    </v-date-picker>
  </v-dialog>
</v-col>